module.exports=class{static formatISO(t){return t.split(" ").length>1?new Date(t.replace(" ","T")).toISOString():new Date(t).toISOString()}static leadZeroes(t){return t.length>1?t:"0"+t}static ddmmyyISO(){let t=new Date,e=t.getDate().toString(),r=(t.getMonth()+1).toString();return`${t.getFullYear().toString()}-${this.leadZeroes(r)}-${this.leadZeroes(e)}`}static ddmmyyyy(t){let e=new Date(t),r=e.getDate().toString(),a=(e.getMonth()+1).toString(),i=e.getFullYear().toString();return`${this.leadZeroes(r)}/${this.leadZeroes(a)}/${i}`}static ddmm(t){let e=new Date(t),r=e.getDate().toString(),a=(e.getMonth()+1).toString();return`${this.leadZeroes(r)}/${this.leadZeroes(a)}`}static hhmm(t){let e=new Date(t),r=e.getHours().toString(),a=e.getMinutes().toString();return`${this.leadZeroes(r)}:${this.leadZeroes(a)}`}static weekDayFull(t){return["domingo","segunda-feira","terça-feira","quarta-feira","quinta-feira","sexta-feira","sábado"][new Date(t).getDay()]}};